package JavaBasics;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		for (int i = 1; i<=20; i++) {
			
			for (int b=1; b <=i; b++) {
				
				System.out.print("* ");
				
			}
				
				System.out.println();
			
		}
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
